import React from "react";
import {Carousel, CarouselItem, Button, Modal, ModalBody, ModalTitle, ModalFooter} from "react-bootstrap";
import "../css/home.css";
import Form from "./form";


class Home extends React.Component {
  constructor() {
    super()
    this.state = {
      show:false
    }
  } 
  handleModal() {
    this.setState({show:!this.state.show})
  }
  render() {
  return (
    <div className="Home">
      <main>
        <Modal show ={this.state.show} class="modal">
          <ModalTitle>Report Sustainability Concern</ModalTitle>
          <Form></Form>
        </Modal>
        <Carousel className = "carousel-container">
          <CarouselItem>
            <img
            className = "carousel-image"
            src = "/stateCapital.jpg" 
            />
            <Carousel.Caption>
            <a href="https://storymaps.arcgis.com/stories/16c9c7788af043b8a304e1289c3300f4"><button type="button" class="carousel-button">View Nashville's Sustainability Story</button></a>
            </Carousel.Caption>
          </CarouselItem>
          <CarouselItem>
            <img
            className = "carousel-image"
            src = "/NashvilleMap.jpg"
            />
            <Carousel.Caption>
            <a href="https://arcg.is/1aTu4X"><button type="button" class="carousel-button">Explore Nashville's Sustainability Map</button></a>
            </Carousel.Caption>
          </CarouselItem>
          <CarouselItem>
            <img
            className = "carousel-image"
            src = "/Landfill.jpg"
            />
            <Carousel.Caption>
            <a><button onClick={()=>{this.handleModal()}} type="button" class="carousel-button">Report A Sustainability Concern</button></a>
            </Carousel.Caption>
          </CarouselItem>
        </Carousel>
      </main>
    </div>
  );
}
}

export default Home;
